void display_update_network_stat(WM_HWIN hWin)
{
	int32_t i;
	char data[64]={0};
	WM_HWIN hItem;

	hItem = WM_GetDialogItem(hWin, ID_LISTVIEW_0);
	for(i=0; i<HTTP_TCP_CEP_NUM + FTP_TCP_CEP_NUM; i++)
	{
		/* set id */
		sprintf(data, "%d", i+1);
		LISTVIEW_SetItemText(hItem, 0, i, data);

		/* set local */
		sprintf(data, "%d.%d.%d.%d:%d", tcpudp_env[0].ipaddr[0], tcpudp_env[0].ipaddr[1], tcpudp_env[0].ipaddr[2], tcpudp_env[0].ipaddr[3], tcp_crep[i].myaddr.portno);
		LISTVIEW_SetItemText(hItem, 1, i, data);

		/* set remote */
		if(i < HTTP_TCP_CEP_NUM)
		{
			sprintf(data, "%d.%d.%d.%d:%d",
					(http_cep[i].dstaddr.ipaddr & 0xff000000) >> 24,
					(http_cep[i].dstaddr.ipaddr & 0x00ff0000) >> 16,
					(http_cep[i].dstaddr.ipaddr & 0x0000ff00) >>  8,
					(http_cep[i].dstaddr.ipaddr & 0x000000ff) >>  0,
					http_cep[i].dstaddr.portno);
		}
		else
		{
			sprintf(data, "%d.%d.%d.%d:%d",
						(ftp_cep[i-FTP_START_TCP_CEP].dstaddr.ipaddr & 0xff000000) >> 24,
						(ftp_cep[i-FTP_START_TCP_CEP].dstaddr.ipaddr & 0x00ff0000) >> 16,
						(ftp_cep[i-FTP_START_TCP_CEP].dstaddr.ipaddr & 0x0000ff00) >>  8,
						(ftp_cep[i-FTP_START_TCP_CEP].dstaddr.ipaddr & 0x000000ff) >>  0,
						ftp_cep[i-FTP_START_TCP_CEP].dstaddr.portno);
		}
		LISTVIEW_SetItemText(hItem, 2, i, data);

		/* set status */
		sprintf(data, "%s", tcp_status_list[tcp_read_stat(i+1)]);
		LISTVIEW_SetItemText(hItem, 3, i, data);

		/* set transmit/receive byte */
		if(i < HTTP_TCP_CEP_NUM)
		{
			sprintf(data, "%d", http_cep[i].now_data);
		}
		else
		{
			sprintf(data, "%d", ftp_cep[i-FTP_START_TCP_CEP].now_data);
		}
		LISTVIEW_SetItemText(hItem, 4, i, data);
	}
}
