void display_update_storagebenchmark(WM_HWIN hWin, uint32_t result)
{
    char text[30];
    float calc_data = DATA_VOLUME*1024*8;
    float bit_rate;

    bit_rate = ((calc_data)/((float)result))*COUNT_FREQENCY;   //1sの間に計算したデータ量

    if((bit_rate/1000000) > 1)
    {
        sprintf(text, "%4.1f Mbps", bit_rate/1000000);
        EDIT_SetText(hWin, text);
    }
    else if((bit_rate/1000) > 1)
	{
        sprintf(text, "%4.1f kbps", bit_rate/1000);
        EDIT_SetText(hWin, text);
	}
    else
    {
        sprintf(text, "%4.1f bps", bit_rate);
        EDIT_SetText(hWin, text);
    }
}
void display_update_storagebenchmark2(WM_HWIN hWin,  uint32_t id, uint32_t result, uint32_t test_time_sec, int32_t okng)
{
    char text[30];
    float calc_data = result * 8;
    float bit_rate;
    int16_t bar_id;
    WM_HWIN hItem_prog;

    bit_rate = calc_data / test_time_sec;   //1sの間に計算したデータ量

    switch(id)
    {
    case 0:
    	bar_id = ID_EDIT_0;
		break;
    case 1:
    	bar_id = ID_EDIT_1;
		break;
    case 2:
    	bar_id = ID_EDIT_2;
		break;
    case 3:
    	bar_id = ID_EDIT_3;
		break;
    }

    hItem_prog = WM_GetDialogItem(hWin, bar_id);

    if(okng == 0)
    {
		if((bit_rate/1000000) > 1)
		{
			sprintf(text, "%4.1f Mbps", bit_rate/1000000);
			EDIT_SetText(hItem_prog, text);
		}
		else if((bit_rate/1000) > 1)
		{
			sprintf(text, "%4.1f kbps", bit_rate/1000);
			EDIT_SetText(hItem_prog, text);
		}
		else
		{
			sprintf(text, "%4.1f bps", bit_rate);
			EDIT_SetText(hItem_prog, text);
		}
    }

    else if(okng == 1)
    {
		EDIT_SetText(hItem_prog, "");
    }
    else
    {
		EDIT_SetText(hItem_prog, "Error!");
    }
}

void display_update_storagebenchmark_bar(WM_HWIN hWin, uint32_t id, int32_t value, uint32_t max)
{
    int16_t bar_id;
    WM_HWIN hItem_prog;

    switch(id)
    {
    case 0:
    	bar_id = ID_PROGBAR_0;
		break;
    case 1:
    	bar_id = ID_PROGBAR_1;
		break;
    case 2:
    	bar_id = ID_PROGBAR_2;
		break;
    case 3:
    	bar_id = ID_PROGBAR_3;
		break;
    }

    hItem_prog = WM_GetDialogItem(hWin, bar_id);
    PROGBAR_SetMinMax(hItem_prog, 0 , max);
    PROGBAR_SetValue(hItem_prog, value);
}

